#include <iostream>
#include <fstream>
#include <string>
#include <sstream>
#include <unordered_map>
#include <regex>

std::unordered_map<std::string, int> countWordOccurrences(const std::string& filename, const std::string& wordToFind) {
    std::ifstream file(filename);
    std::unordered_map<std::string, int> wordCount;
    std::regex wordRegex("(\\b" + std::regex_replace(wordToFind, std::regex("([a-z0-9_]+)"), "\\$0") + "\\b", std::regex_constants::icase);

    std::string line;
    while (std::getline(file, line)) {
        std::sregex_iterator wordsBegin(line.begin(), line.end(), wordRegex), wordsEnd;
        for (std::sregex_iterator i = wordsBegin; i!= wordsEnd; ++i) {
            if (*i.str() == wordToFind) {
                wordCount[*i.str()] += 1;
            }
        }
    }

    return wordCount;
}

int main(int argc, char* argv[]) {
    if (argc!= 3) {
        std::cerr << "Usage: " << argv[0] << " <filename> <wordToCount>" << std::endl;
        return 1;
    }

    const std::string filename = argv[1];
    const std::string wordToFind = argv[2];

    auto occurrences = countWordOccurrences(filename, wordToFind);
    std::cout << "The word '" << wordToFind << "' appears " << occurrences[wordToFind] << " times in the file '" << filename << "'\n";

    return 0;
}
