    if (num <= 1) {
        return false; // 1 or less is not prime
    }
    if (num <= 3) {
        return true; // 2 and 3 are prime
    }
    if (num % 2 === 0 || num % 3 === 0) {
        return false; // exclude numbers divisible by 2 or 3
    }
    // Check if the number is divisible by any number up to the square root of the number
