def longest_common_subsequence(s1, s2):
    # Create a matrix to store lengths of longest common subsequences for substrings
    dp = [[0] * (len(s2) + 1) for _ in range(len(s1) + 1)]

    # Iterate through both strings and fill the matrix
    for i in range(1, len(s1) + 1):
        for j in range(1, len(s2) + 1):
            if s1[i - 1] == s2[j - 1]:
                dp[i][j] = dp[i - 1][j - 1] + 1
            else:
                dp[i][j] = max(dp[i - 1][j], dp[i][j - 1])

    # Reconstruct the longest common subsequence from the matrix
    lcs = []
    i, j = len(s1), len(s2)
    while i > 0 and j > 0:
        if s1[i - 1] == s2[j - 1]:
            lcs.append(s1[i - 1])
            i -= 1
            j -= 1
        elif dp[i - 1][j] > dp[i][j - 1]:
            j -= 1
        else:
            i -= 1

    return ''.join(reversed(lcs))


# Test example
s1 = "ABCBDAB"
s2 = "BDCABA"
print(longest_common_subsequence(s1, s2))  # Output: "BCAB"
