    string src = markdown.substr(pos + 2, markdown.find("]", pos + 2) - pos - 2);
    string alt = markdown.substr(markdown.find("]", pos + 2) + 2, markdown.find("\n", markdown.find("]", pos + 2) + 2) - markdown.find("]", pos + 2) - 2);
    html += "<img src=\"" + src + "\" alt=\"" + alt + "\">";
    pos = markdown.find("\n", pos + 1);
  }

  // Convert code blocks
  pos = 0;
  while ((pos = markdown.find("```", pos)) != string::npos) {
    string language = markdown.substr(pos + 3, markdown.find("\n", pos + 3) - pos - 3);
    string code = markdown.substr(markdown.find("\n", pos + 3) + 1, markdown.find("```", markdown.find("\n", pos + 3) + 1) - markdown.find("\n", pos + 3) - 1);
    html += "<pre><code class=\"language-" + language + "\">" + code + "</code></pre>";
