const { Client, GatewayIntentBits } = require('discord.js');
const client = new Client({ intents: [GatewayIntentBits.Guilds, GatewayIntentBits.GuildMembers] });

client.once('ready', () => {
  console.log('Ready!');
});

client.on('interactionCreate', async interaction => {
  if (!interaction.isChatInputCommand()) return;

  if (interaction.commandName === 'ban') {
    const member = interaction.options.getMember('member');
    const reason = interaction.options.getString('reason');

    if (!member) {
      await interaction.reply({ content: 'Please specify a member to ban.', ephemeral: true });
      return;
    }

    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      await interaction.reply({ content: 'You do not have permission to ban members.', ephemeral: true });
      return;
    }

    try {
      await member.ban({ reason: reason });
      await interaction.reply({ content: `Banned ${member.user.tag} for ${reason}.` });
    } catch (error) {
      console.error(error);
      await interaction.reply({ content: 'Failed to ban member.', ephemeral: true });
    }
  }
});

client.commands = new Discord.Collection();
client.commands.add({
  name: 'ban',
  description: 'Ban a member from the server.',
  options: [
    {
      name: 'member',
      description: 'The member to ban.',
      type: 6, // USER
      required: true,
    },
    {
      name: 'reason',
      description: 'The reason for the ban.',
      type: 3, // STRING
      required: false,
    },
  ],
});

client.login('YOUR_BOT_TOKEN');
```
