#include <SDL.h>

void render_game_board(SDL_Renderer* renderer, SDL_Surface* snake_head_image, SDL_Surface* snake_body_image, SDL_Surface* food_image, SDL_Surface* game_over_image, int snake_x, int snake_y, int snake_length, int food_x, int food_y) {
    // Clear screen
    SDL_RenderClear(renderer);

    // Draw game board grid
    for (int i = 0; i < GAME_WIDTH; i += GRID_SIZE) {
        for (int j = 0; j < GAME_HEIGHT; j += GRID_SIZE) {
            SDL_Rect rect = {i, j, GRID_SIZE, GRID_SIZE};
            SDL_SetRenderDrawColor(renderer, 0x00, 0x00, 0x00, 0xFF);
            SDL_RenderFillRect(renderer, &rect);
        }
    }

    // Draw snake
    for (int i = 0; i < snake_length; i++) {
        int x = snake_x - (i * GRID_SIZE);
        int y = snake_y;
        SDL_Rect rect = {x, y, GRID_SIZE, GRID_SIZE};
        SDL_BlitSurface(snake_body_image, NULL, renderer, &rect);
    }
    SDL_Rect head_rect = {snake_x, snake_y, GRID_SIZE, GRID_SIZE};
    SDL_BlitSurface(snake_head_image, NULL, renderer, &head_rect);

    // Draw food
    SDL_Rect food_rect = {food_x, food_y, GRID_SIZE, GRID_SIZE};
    SDL_BlitSurface(food_image, NULL, renderer, &food_rect);

    // Draw game over screen
    if (game_over) {
        SDL_Rect game_over_rect = {0, 0, GAME_WIDTH, GAME_HEIGHT};
        SDL_BlitSurface(game_over_image, NULL, renderer, &game_over_rect);
    }
}
