function isPrime(num) {
  // Check if number is less than 2 (not prime)
  if (num < 2) {
    return false;
  }
  // Check for factors from 2 to the square root of the number
  for (let i = 2; i <= Math.sqrt(num); i++) {
    if (num % i === 0) {
      return false; // Number is divisible, not prime
    }
  }
  return true; // No divisors found, number is prime
}

function checkForPrimes(numbers) {
  // Check if the input is an array and has exactly 5 elements
  if (Array.isArray(numbers) && numbers.length === 5) {
    numbers.forEach(num => {
      // Check if the number is prime
      if (isPrime(num)) {
        // Print the prime number to the console
        console.log(num);
      }
    });
  } else {
    console.log("Input must be an array containing 5 numbers.");
  }
}

// Example usage:
